import React from "react";
import { Redirect } from "react-router-dom";
import restaurants from "./RestaurantData";
import RestaurantListing from "./RestaurantListing";

class SearchCity extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      restaurants: [],
      searchTerm: ""
    };
    this.addBucketListItem = this.addBucketListItem.bind(this);
  }

  handleUpdateSearchTerm(searchTerm) {
    this.setState({ searchTerm });
  }

  addBucketListItem(item) {
    this.props.updateProfile({
      ...this.props.profile,
      bucketList: [...this.props.profile.bucketList, item],
    });
  }

  componentDidMount() {
    fetch('http://localhost:8000/searchcity')
      .then(response => response.json())
      .then(restaurants => {
        console.log(restaurants);
        this.setState({ restaurants })
      });
  }

  render() {
    return (
      <div>
        <form>
          <br></br>
          <label for="city">Enter a City:</label>
          <br></br>
          <input
            type="text"
            id="searchTerm"
            value={this.state.searchTerm}
            onChange={(event) =>
              this.handleUpdateSearchTerm(event.target.value)
            }
          ></input>
          <br></br>

          <div>
            {this.state.restaurants
              .filter((restaurant) => {
                if (this.state.searchTerm == "") {
                  return restaurant;
                } else if (
                  restaurant.city
                    .toLowerCase()
                    .includes(this.state.searchTerm.toLowerCase())
                ) {
                  return restaurant;
                }
              })
              .map((restaurant, key) => {
                return (
                  <RestaurantListing
                    restaurant={restaurant}
                    loggedInUser={this.props.profile}
                    addBucketListItem={this.addBucketListItem}
                  />
                );
              })}
          </div>
        </form>
      </div>
    );
  }
}

export default SearchCity;

